import React, { useEffect, useState } from 'react';
import CommonSection from '../Shared/CommonSection';
import { Container, Row, Col } from 'reactstrap';
import BookingCard from '../Shared/BookingCard';
import useFetch from '../hooks/useFetch.js';
import { BASE_URL } from '../utils/config.js';

const MyBookings = () => {
    const [bookings, setBookings] = useState([]);
    const [loading, setLoading] = useState(true);

    const { data: user } = useFetch(`${BASE_URL}/auth/login`);
    console.log(user);
    const { data: allBookings, loading: allLoading } = useFetch(`${BASE_URL}/bookings/user/${user._id}`);

    useEffect(() => {
        if (!allLoading && allBookings) {
            setBookings(allBookings);
            setLoading(false);
        }
    }, [allBookings, allLoading]);

    return (
        <>
            <CommonSection title="My Bookings" />
            <section className="py-5">
                <Container>
                    {loading && <h4 className="text-center">Loading.........</h4>}
                    {!loading && (
                        <>
                            {bookings.length > 0 ? (
                                <Row>
                                    {bookings.map((booking) => (
                                        <Col lg="4" md="6" className="mb-4" key={booking._id}>
                                            <BookingCard booking={booking} />
                                        </Col>
                                    ))}
                                </Row>
                            ) : (
                                <h4 className="text-center">You haven't made any bookings yet.</h4>
                            )}
                        </>
                    )}
                </Container>
            </section>
        </>
    );
};

export default MyBookings;
