import React, { useState, useEffect } from 'react';
import { Card, CardBody } from 'reactstrap';
import axios from 'axios';

const MyBooking = () => {
    const [bookings, setBookings] = useState([]);

    useEffect(() => {
        const fetchBookings = async () => {
            try {
                const res = await axios.get('/api/bookings/user/:userId'); // replace userId with actual user ID
                setBookings(res.data.data);
            } catch (err) {
                console.log(err);
            }
        };

        fetchBookings();
    }, []);

    return (
        <div className="my-booking">
            <h1>My Bookings</h1>
            {bookings.length === 0 && <p>No bookings yet.</p>}
            {bookings.map((booking) => (
                <Card key={booking._id} className="mb-3">
                    <div className="hotel__img">
                        <img src={booking.photo} alt="hotel-img" />
                    </div>
                    <CardBody>
                        <div className="card__top d-flex align-items-center justify-content-between">
                            <h5 className="hotel__title">{booking.title}</h5>
                            <span>${booking.price} /day</span>
                        </div>
                        <p>
                            <strong>Location: </strong>
                            {booking.city}
                        </p>
                        <p>
                            <strong>Check-in: </strong>
                            {new Date(booking.checkin).toLocaleDateString()}
                        </p>
                        <p>
                            <strong>Check-out: </strong>
                            {new Date(booking.checkout).toLocaleDateString()}
                        </p>
                    </CardBody>
                </Card>
            ))}
        </div>
    );
};

export default MyBooking;
